            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
              <Card>
                <CardHeader>
                  <CardTitle>Event Performance</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-slate-600">Most Popular Event</span>
                      <span className="font-semibold">Tech Conference 2024</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-slate-600">Average Attendance Rate</span>
                      <span className="font-semibold">78%</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-slate-600">Revenue Growth</span>
                      <span className="font-semibold text-green-600">+15%</span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Monthly Trends</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-slate-600">Events This Month</span>
                      <span className="font-semibold">{events?.length || 0}</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-slate-600">New Registrations</span>
                      <span className="font-semibold">{stats?.totalAttendees || 0}</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-slate-600">Revenue This Month</span>
                      <span className="font-semibold">${stats?.totalRevenue?.toLocaleString() || 0}</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          </main>
        )}

        {/* User View */}
        {activeTab === "users" && (
          <main className="p-6">
            <div className="text-center">
              <Button 
                onClick={() => window.location.href = "/events"}
                className="bg-primary-500 hover:bg-primary-600"
              >
                Switch to User View
              </Button>
            </div>
          </main>
        )}
      </div>

      <CreateEventModal 
        isOpen={isCreateModalOpen} 
        onClose={() => setIsCreateModalOpen(false)} 
      />

      {editingEvent && (
        <EditEventModal 
          isOpen={!!editingEvent} 
          onClose={() => setEditingEvent(null)} 
          event={editingEvent}
        />
      )}
    </div>
  );
}

client/src/pages/auth-page.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Redirect } from "wouter";
import { Calendar, Users, Trophy } from "lucide-react";

const loginSchema = z.object({
  email: z.string().email("Invalid email address"),
  password: z.string().min(1, "Password is required"),
});

const registerSchema = z.object({
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  username: z.string().min(3, "Username must be at least 3 characters"),
});

type LoginFormData = z.infer<typeof loginSchema>;
type RegisterFormData = z.infer<typeof registerSchema>;

export default function AuthPage() {
  const { user, isLoading, loginMutation, registerMutation } = useAuth();
  const [activeTab, setActiveTab] = useState("login");

  const loginForm = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const registerForm = useForm<RegisterFormData>({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      email: "",
      password: "",
      firstName: "",
      lastName: "",
      username: "",
    },
  });

  // Redirect if already logged in
  if (user) {
    return <Redirect to="/" />;
  }

  const onLogin = (data: LoginFormData) => {
    loginMutation.mutate(data);
  };

  const onRegister = (data: RegisterFormData) => {
    registerMutation.mutate(data);
  };

  const handleAuth0Login = () => {
    window.location.href = "/api/auth/login";
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800 flex">
      {/* Left side - Hero Section */}
      <div className="hidden lg:flex lg:w-1/2 flex-col justify-center p-12 bg-gradient-to-br from-blue-600 to-indigo-700 dark:from-blue-800 dark:to-indigo-900 text-white">
        <div className="space-y-8">
          <div className="space-y-4">
            <h1 className="text-4xl font-bold leading-tight">
              Welcome to EventMaster
            </h1>
            <p className="text-xl text-blue-100">
              Your complete event management and ticketing platform
            </p>
          </div>

          <div className="space-y-6">
            <div className="flex items-center space-x-4">
              <div className="bg-white/20 rounded-lg p-3">
                <Calendar className="h-6 w-6" />
              </div>
              <div>
                <h3 className="font-semibold">Event Management</h3>
                <p className="text-blue-100">Create and manage events with ease</p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <div className="bg-white/20 rounded-lg p-3">
                <Users className="h-6 w-6" />
              </div>
              <div>
                <h3 className="font-semibold">Attendee Management</h3>
                <p className="text-blue-100">Track bookings and manage attendees</p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <div className="bg-white/20 rounded-lg p-3">
                <Trophy className="h-6 w-6" />
              </div>
              <div>
                <h3 className="font-semibold">Analytics & Insights</h3>
                <p className="text-blue-100">Get detailed insights into your events</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Right side - Auth Forms */}
      <div className="w-full lg:w-1/2 flex items-center justify-center p-8">
        <div className="w-full max-w-md space-y-6">
          <div className="text-center">
            <h2 className="text-3xl font-bold text-gray-900 dark:text-white">
              Get Started
            </h2>
            <p className="mt-2 text-gray-600 dark:text-gray-400">
              Sign in to your account or create a new one
            </p>
          </div>

          <Card>
            <CardHeader className="text-center">
              <CardTitle>Authentication</CardTitle>
              <CardDescription>
                Choose your preferred sign-in method
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Auth0 Login Button */}
              <Button 
                onClick={handleAuth0Login}
                className="w-full"
                variant="outline"
              >
                Continue with Auth0
              </Button>

              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <span className="w-full border-t" />
                </div>
                <div className="relative flex justify-center text-xs uppercase">
                  <span className="bg-background px-2 text-muted-foreground">
                    Or continue with
                  </span>
                </div>
              </div>

              {/* Local Auth Tabs */}
              <Tabs value={activeTab} onValueChange={setActiveTab}>
                <TabsList className="grid w-full grid-cols-2">
                  <TabsTrigger value="login">Sign In</TabsTrigger>
                  <TabsTrigger value="register">Sign Up</TabsTrigger>
                </TabsList>

                <TabsContent value="login" className="space-y-4">
                  <Form {...loginForm}>
                    <form onSubmit={loginForm.handleSubmit(onLogin)} className="space-y-4">
                      <FormField
                        control={loginForm.control}
                        name="email"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter your email" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={loginForm.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Password</FormLabel>
                            <FormControl>
                              <Input type="password" placeholder="Enter your password" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <Button 
                        type="submit" 
                        className="w-full"
                        disabled={loginMutation.isPending}
                      >
                        {loginMutation.isPending ? "Signing in..." : "Sign In"}
                      </Button>
                    </form>
                  </Form>

                  <div className="text-center text-sm text-gray-500">
                    <p>Demo admin account:</p>
                    <p>Email: admin@eventmaster.com</p>
                    <p>Password: admin123</p>
                  </div>
                </TabsContent>

                <TabsContent value="register" className="space-y-4">
                  <Form {...registerForm}>
                    <form onSubmit={registerForm.handleSubmit(onRegister)} className="space-y-4">
                      <div className="grid grid-cols-2 gap-4">
                        <FormField
                          control={registerForm.control}
                          name="firstName"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>First Name</FormLabel>
                              <FormControl>
                                <Input placeholder="First name" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={registerForm.control}
                          name="lastName"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Last Name</FormLabel>
                              <FormControl>
                                <Input placeholder="Last name" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <FormField
                        control={registerForm.control}
                        name="username"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Username</FormLabel>
                            <FormControl>
                              <Input placeholder="Choose a username" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={registerForm.control}
                        name="email"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter your email" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={registerForm.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Password</FormLabel>
                            <FormControl>
                              <Input type="password" placeholder="Create a password" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <Button 
                        type="submit" 
                        className="w-full"
                        disabled={registerMutation.isPending}
                      >
                        {registerMutation.isPending ? "Creating account..." : "Create Account"}
                      </Button>
                    </form>
                  </Form>
                </TabsContent>
              </Tabs>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/event-details.tsx.content:
import { useState } from "react";
import { useParams } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Calendar, MapPin, Users, Clock, ArrowLeft, Ticket } from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";

export default function EventDetails() {
  const { id } = useParams();
  const { user } = useAuth();
  const { toast } = useToast();
  const [ticketQuantity, setTicketQuantity] = useState(1);

  // Define authentication state
  const isAuthenticated = !!user;

  const { data: event, isLoading } = useQuery({
    queryKey: ["/api/events", id],
    enabled: !!id,
    retry: false,
  });

  // Type guard for event
  const eventData = event as any;

  const bookingMutation = useMutation({
    mutationFn: async (bookingData: any) => {
      const response = await apiRequest("POST", "/api/bookings", bookingData);
      return response.json();
    },
    onSuccess: (booking) => {
      toast({
        title: "Booking Confirmed!",
        description: `Your booking reference is ${booking.bookingReference}. Email confirmation sent!`,
      });

      // Invalidate cache to refresh event data and attendee counts
      queryClient.invalidateQueries({ queryKey: ["/api/events"] });
      queryClient.invalidateQueries({ queryKey: ["/api/events", id] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });

      // Navigate to checkout using MongoDB ObjectId
      const bookingId = booking._id || booking.id;
      console.log('Redirecting to checkout with booking ID:', bookingId);
      window.location.href = `/checkout/${bookingId}`;
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Please sign in",
          description: "You need to sign in to book tickets.",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1000);
        return;
      }

      toast({
        title: "Booking Failed",
        description: error.message || "Failed to create booking",
        variant: "destructive",
      });
    },
  });

  const handleBookTickets = () => {
    if (!isAuthenticated) {
      toast({
        title: "Please sign in",
        description: "You need to sign in to book tickets.",
        variant: "destructive",
      });
      window.location.href = "/api/login";
      return;
    }

    if (!eventData) return;

    const totalAmount = parseFloat(eventData.ticketPrice) * ticketQuantity;

    bookingMutation.mutate({
      eventId: eventData.id,
      quantity: ticketQuantity,
      totalAmount: totalAmount.toString(),
      status: "pending",
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (!eventData) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-slate-50">
        <Card className="w-full max-w-md mx-4">
          <CardContent className="pt-6">
            <div className="text-center">
              <h1 className="text-2xl font-bold text-slate-800 mb-4">Event Not Found</h1>
              <p className="text-slate-600 mb-4">The event you're looking for doesn't exist.</p>
              <Button onClick={() => window.location.href = "/events"}>
                Back to Events
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  const availableTickets = eventData.maxAttendees - eventData.currentAttendees;
  const isEventFull = availableTickets <= 0;
  const isEventPast = new Date(eventData.endDate) < new Date();

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Navigation */}
      <nav className="bg-white shadow-sm border-b border-slate-200 px-6 py-4">
        <div className="max-w-7xl mx-auto flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button 
              variant="ghost" 
              onClick={() => window.history.back()}
              className="flex items-center space-x-2"
            >
              <ArrowLeft className="h-4 w-4" />
              <span>Back</span>
            </Button>
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-primary-500 rounded-lg flex items-center justify-center">
                <Calendar className="text-white h-5 w-5" />
              </div>
              <h1 className="text-xl font-bold text-slate-800">EventMaster</h1>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            {user ? (
              <div className="flex items-center space-x-3">
                <span className="text-sm font-medium text-slate-700">
                  {user.firstName} {user.lastName}
                </span>
                <Button 
                  size="sm"
                  onClick={() => window.location.href = "/api/logout"}
                  variant="outline"
                >
                  Logout
                </Button>
              </div>
            ) : (
              <Button onClick={() => window.location.href = '/auth-choice'}>
                Sign In
              </Button>
            )}
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto px-6 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Event Details */}
          <div className="lg:col-span-2 space-y-6">
            {/* Event Image */}
            <div className="aspect-video rounded-2xl overflow-hidden">
              <img 
                src={eventData.imageUrl || "https://images.unsplash.com/photo-1540575467063-178a50c2df87?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=400"}
                alt={eventData.name}
                className="w-full h-full object-cover"
              />
            </div>

            {/* Event Info */}
            <Card>
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div>
                    <CardTitle className="text-3xl font-bold text-slate-800 mb-2">
                      {eventData.name}
                    </CardTitle>
                    <Badge variant="secondary" className="mb-4">
                      {eventData.category}
                    </Badge>
                  </div>
                  <Badge 
                    variant={eventData.status === 'active' ? 'default' : 'secondary'}
                    className={
                      eventData.status === 'active' ? 'bg-green-100 text-green-800' :
                      eventData.status === 'upcoming' ? 'bg-blue-100 text-blue-800' :
                      eventData.status === 'completed' ? 'bg-gray-100 text-gray-800' :
                      'bg-yellow-100 text-yellow-800'
                    }
                  >
                    {eventData.status}
                  </Badge>
                </div>
              </CardHeader>
              <CardContent className="space-y-6">
                <p className="text-lg text-slate-600 leading-relaxed">
                  {eventData.description}
                </p>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="flex items-center space-x-3">
                    <Calendar className="h-5 w-5 text-slate-400" />
                    <div>
                      <p className="font-medium text-slate-800">Start Date</p>
                      <p className="text-slate-600">
                        {new Date(eventData.startDate).toLocaleDateString()} at{" "}
                        {new Date(eventData.startDate).toLocaleTimeString()}
                      </p>
                    </div>
                  </div>

                  <div className="flex items-center space-x-3">
                    <Clock className="h-5 w-5 text-slate-400" />
                    <div>
                      <p className="font-medium text-slate-800">End Date</p>
                      <p className="text-slate-600">
                        {new Date(eventData.endDate).toLocaleDateString()} at{" "}
                        {new Date(eventData.endDate).toLocaleTimeString()}
                      </p>
                    </div>
                  </div>

                  <div className="flex items-center space-x-3">
                    <MapPin className="h-5 w-5 text-slate-400" />
                    <div>
                      <p className="font-medium text-slate-800">Location</p>
                      <p className="text-slate-600">{eventData.location}</p>
                    </div>
                  </div>

                  <div className="flex items-center space-x-3">
                    <Users className="h-5 w-5 text-slate-400" />
                    <div>
                      <p className="font-medium text-slate-800">Capacity</p>
                      <p className="text-slate-600">
                        {eventData.currentAttendees} / {eventData.maxAttendees} attendees
                      </p>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Booking Card */}
          <div className="space-y-6">
            <Card className="sticky top-6">
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Ticket className="h-5 w-5" />
                  <span>Book Tickets</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="text-center">
                  <p className="text-3xl font-bold text-slate-800">
                    ${parseFloat(eventData.ticketPrice).toFixed(2)}
                  </p>
                  <p className="text-slate-600">per ticket</p>
                </div>

                {!isEventPast && !isEventFull && eventData.status !== 'draft' ? (
                  <>
                    <div className="space-y-2">
                      <Label htmlFor="quantity">Number of Tickets</Label>
                      <Input
                        id="quantity"
                        type="number"
                        min="1"
                        max={Math.min(10, availableTickets)}
                        value={ticketQuantity}
                        onChange={(e) => setTicketQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                      />
                      <p className="text-sm text-slate-500">
                        {availableTickets} tickets available
                      </p>
                    </div>

                    <div className="border-t pt-4">
                      <div className="flex justify-between text-lg font-semibold">
                        <span>Total:</span>
                        <span>${(parseFloat(eventData.ticketPrice) * ticketQuantity).toFixed(2)}</span>
                      </div>
                    </div>

                    <Button 
                      onClick={handleBookTickets}
                      disabled={bookingMutation.isPending}
                      className="w-full bg-primary-500 hover:bg-primary-600"
                      size="lg"
                    >
                      {bookingMutation.isPending ? (
                        <>
                          <div className="animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2" />
                          Processing...
                        </>
                      ) : (
                        "Book Now"
                      )}
                    </Button>

                    {!isAuthenticated && (
                      <p className="text-sm text-slate-500 text-center">
                        You'll be asked to sign in before completing your booking
                      </p>
                    )}
                  </>
                ) : (
                  <div className="text-center py-4">
                    {isEventPast ? (
                      <p className="text-slate-600">This event has ended</p>
                    ) : isEventFull ? (
                      <p className="text-slate-600">This event is sold out</p>
                    ) : eventData.status === 'draft' ? (
                      <p className="text-slate-600">This event is not yet available for booking</p>
                    ) : (
                      <p className="text-slate-600">Booking is not available</p>
                    )}
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Event Stats */}
            <Card>
              <CardHeader>
                <CardTitle>Event Stats</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex justify-between">
                    <span className="text-slate-600">Tickets Sold</span>
                    <span className="font-medium">{eventData.currentAttendees}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-slate-600">Available</span>
                    <span className="font-medium">{availableTickets}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-slate-600">Total Capacity</span>
                    <span className="font-medium">{eventData.maxAttendees}</span>
                  </div>
                  <div className="w-full bg-slate-200 rounded-full h-2">
                    <div 
                      className="bg-primary-500 h-2 rounded-full" 
                      style={{ width: `${(eventData.currentAttendees / eventData.maxAttendees) * 100}%` }}
                    />
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

