server/mongodb-storage.ts.content:
import mongoose from 'mongoose';
import { connectToDatabase } from './mongodb';
import { 
  UserModel, 
  EventModel, 
  BookingModel, 
  NotificationModel, 
  ContactMessageModel,
  type User,
  type Event,
  type Booking,
  type Notification,
  type ContactMessage,
  type UpsertUser,
  type InsertEvent,
  type InsertBooking,
  type InsertNotification,
  type InsertContactMessage,
  type EventWithBookings,
  type BookingWithEvent
} from '@shared/mongodb-schema';
import type { IStorage } from './storage';

export class MongoStorage implements IStorage {
  constructor() {
    connectToDatabase();
  }

  // User operations  
  async getUser(id: string): Promise<User | undefined> {
    try {
      const user = await UserModel.findById(id).exec();
      if (!user) return undefined;

      const userObj = user.toObject();
      return {
        id: userObj._id.toString(),
        email: userObj.email || '',
        username: userObj.username || null,
        password: userObj.password || null,
        firstName: userObj.firstName || null,
        lastName: userObj.lastName || null,
        profileImageUrl: userObj.profileImageUrl || null,
        isAdmin: Boolean(userObj.isAdmin),
        stripeCustomerId: userObj.stripeCustomerId || null,
        authProvider: userObj.authProvider || 'local',
        authProviderId: userObj.authProviderId || null,
        createdAt: userObj.createdAt || new Date(),
        updatedAt: userObj.updatedAt || new Date(),
      };
    } catch (error) {
      console.error('Error getting user:', error);
      return undefined;
    }
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    try {
      const user = await UserModel.findOne({ email }).exec();
      if (!user) return undefined;

      const userObj = user.toObject();
      return {
        id: userObj._id.toString(),
        email: userObj.email || '',
        username: userObj.username || null,
        password: userObj.password || null,
        firstName: userObj.firstName || null,
        lastName: userObj.lastName || null,
        profileImageUrl: userObj.profileImageUrl || null,
        isAdmin: Boolean(userObj.isAdmin),
        stripeCustomerId: userObj.stripeCustomerId || null,
        authProvider: userObj.authProvider || 'local',
        authProviderId: userObj.authProviderId || null,
        createdAt: userObj.createdAt || new Date(),
        updatedAt: userObj.updatedAt || new Date(),
      };
    } catch (error) {
      console.error('Error getting user by email:', error);
      return undefined;
    }
  }

  async getUsers(): Promise<User[]> {
    try {
      const users = await UserModel.find().sort({ createdAt: -1 }).exec();
      return users.map(user => {
        const userObj = user.toObject();
        return {
          id: userObj._id.toString(),
          email: userObj.email || '',
          username: userObj.username || null,
          password: userObj.password || null,
          firstName: userObj.firstName || null,
          lastName: userObj.lastName || null,
          profileImageUrl: userObj.profileImageUrl || null,
          isAdmin: Boolean(userObj.isAdmin),
          stripeCustomerId: userObj.stripeCustomerId || null,
          authProvider: userObj.authProvider || 'local',
          authProviderId: userObj.authProviderId || null,
          createdAt: userObj.createdAt || new Date(),
          updatedAt: userObj.updatedAt || new Date(),
        };
      });
    } catch (error) {
      console.error('Error getting users:', error);
      return [];
    }
  }

  async upsertUser(userData: Partial<UpsertUser> & { id: string }): Promise<User> {
    try {
      const updateData = {
        ...userData,
        updatedAt: new Date()
      };

      const user = await UserModel.findByIdAndUpdate(
        userData.id,
        updateData,
        { new: true, upsert: true, setDefaultsOnInsert: true }
      ).exec();

      const userObj = user!.toObject();
      return {
        id: userObj._id.toString(),
        email: userObj.email || '',
        username: userObj.username || null,
        password: userObj.password || null,
        firstName: userObj.firstName || null,
        lastName: userObj.lastName || null,
        profileImageUrl: userObj.profileImageUrl || null,
        isAdmin: Boolean(userObj.isAdmin),
        stripeCustomerId: userObj.stripeCustomerId || null,
        authProvider: userObj.authProvider || 'local',
        authProviderId: userObj.authProviderId || null,
        createdAt: userObj.createdAt || new Date(),
        updatedAt: userObj.updatedAt || new Date(),
      };
    } catch (error) {
      console.error('Error upserting user:', error);
      throw error;
    }
  }

  async createUser(userData: Partial<UpsertUser> & { email: string }): Promise<User> {
    try {
      const id = new mongoose.Types.ObjectId().toString();
      const user = new UserModel({
        _id: id,
        ...userData,
        createdAt: new Date(),
        updatedAt: new Date()
      });

      await user.save();
      const userObj = user.toObject();
      return {
        id: userObj._id.toString(),
        email: userObj.email || '',
        username: userObj.username || null,
        password: userObj.password || null,
        firstName: userObj.firstName || null,
        lastName: userObj.lastName || null,
        profileImageUrl: userObj.profileImageUrl || null,
        isAdmin: Boolean(userObj.isAdmin),
        stripeCustomerId: userObj.stripeCustomerId || null,
        authProvider: userObj.authProvider || 'local',
        authProviderId: userObj.authProviderId || null,
        createdAt: userObj.createdAt || new Date(),
        updatedAt: userObj.updatedAt || new Date(),
      };
    } catch (error) {
      console.error('Error creating user:', error);
      throw error;
    }
  }

  // Event operations
  async createEvent(eventData: any): Promise<Event> {
    try {
      const id = new mongoose.Types.ObjectId().toString();
      const event = new EventModel({
        _id: id,
        ...eventData,
        organizerId: eventData.createdBy || eventData.organizerId,
        createdBy: eventData.createdBy || eventData.organizerId,
        currentAttendees: eventData.currentAttendees || 0,
        createdAt: new Date(),
        updatedAt: new Date()
      });

      await event.save();
      const eventObj = event.toObject();
      return {
        id: parseInt(eventObj._id.toString()) || 0,
        name: eventObj.name || '',
        description: eventObj.description || null,
        startDate: eventObj.startDate || new Date(),
        endDate: eventObj.endDate || new Date(),
        location: eventObj.location || '',
        ticketPrice: eventObj.ticketPrice || '0',
        maxAttendees: eventObj.maxAttendees || 0,
        currentAttendees: eventObj.currentAttendees || 0,
        category: eventObj.category || '',
        status: eventObj.status || 'draft',
        imageUrl: eventObj.imageUrl || null,
        createdBy: eventObj.createdBy || '',
        createdAt: eventObj.createdAt || new Date(),
        updatedAt: eventObj.updatedAt || new Date(),
      };
    } catch (error) {
      console.error('Error creating event:', error);
      throw error;
    }
  }

  async getEvents(filters?: { search?: string; category?: string; status?: string }): Promise<Event[]> {
    try {
      let query: any = {};

      if (filters?.search) {
        query.$or = [
          { name: { $regex: filters.search, $options: 'i' } },
          { description: { $regex: filters.search, $options: 'i' } }
        ];
      }

      if (filters?.category) {
        query.category = filters.category;
      }

      if (filters?.status) {
        query.status = filters.status;
      }

      const events = await EventModel.find(query).sort({ startDate: 1 }).exec();
      return events.map(event => {
        const eventObj = event.toObject();
        return {
          id: parseInt(eventObj._id.toString()) || 0,
          name: eventObj.name || '',
          description: eventObj.description || null,
          startDate: eventObj.startDate || new Date(),
          endDate: eventObj.endDate || new Date(),
          location: eventObj.location || '',
          ticketPrice: eventObj.ticketPrice || '0',
          maxAttendees: eventObj.maxAttendees || 0,
          currentAttendees: eventObj.currentAttendees || 0,
          category: eventObj.category || '',
          status: eventObj.status || 'draft',
          imageUrl: eventObj.imageUrl || null,
          createdBy: eventObj.createdBy || eventObj.organizerId,
          createdAt: eventObj.createdAt,
          updatedAt: eventObj.updatedAt,
        };
      });
    } catch (error) {
      console.error('Error getting events:', error);
      return [];
    }
  }

  async getEvent(id: number): Promise<any> {
    try {
      // Get all events and find the one that matches the numeric ID
      const events = await this.getEvents({});
      const event = events.find(e => e.id === id);

      if (!event) return undefined;

      // Get bookings for this event
      const bookings = await BookingModel.find({ 
        eventId: event.id.toString() 
      }).exec();

      return {
        ...event,
        bookings: bookings.map(booking => {
          const bookingObj = booking.toObject();
          return {
            id: parseInt(bookingObj._id) || bookingObj._id,
            eventId: parseInt(bookingObj.eventId) || bookingObj.eventId,
            userId: bookingObj.userId,
            quantity: bookingObj.quantity,
            totalAmount: bookingObj.totalAmount,
            status: bookingObj.status,
            stripePaymentIntentId: bookingObj.stripePaymentIntentId,
            bookingReference: bookingObj.bookingReference,
            attendeeEmail: bookingObj.attendeeEmail,
            attendeeName: bookingObj.attendeeName,
            createdAt: bookingObj.createdAt,
            updatedAt: bookingObj.updatedAt,
          };
        })
      };
    } catch (error) {
      console.error('Error getting event:', error);
      return undefined;
    }
  }

  async updateEvent(id: number, updates: Partial<InsertEvent>): Promise<Event> {
    try {
      const event = await EventModel.findByIdAndUpdate(
        id.toString(),
        { ...updates, updatedAt: new Date() },
        { new: true }
      ).exec();

      if (!event) throw new Error('Event not found');
      return { ...event.toObject(), id: event._id };
    } catch (error) {
      console.error('Error updating event:', error);
      throw error;
    }
  }

  async deleteEvent(id: number): Promise<void> {
    try {
      await EventModel.findByIdAndDelete(id.toString()).exec();
    } catch (error) {
      console.error('Error deleting event:', error);
      throw error;
    }
  }

  async updateEventAttendeeCount(eventId: number): Promise<void> {
    try {
      console.log('ðŸŽ« Updating attendee count for event ID:', eventId);

      const confirmedBookings = await BookingModel.find({
        eventId: eventId.toString(),
        status: 'confirmed'
      }).exec();

      console.log('ðŸ“Š Found confirmed bookings:', confirmedBookings.length, confirmedBookings.map(b => ({ id: b._id, quantity: b.quantity })));

      const totalAttendees = confirmedBookings.reduce((sum, booking) => {
        return sum + booking.quantity;
      }, 0);

      console.log('ðŸ”¢ Total attendees calculated:', totalAttendees);

      // Find the correct event document by searching all events
      const allEvents = await EventModel.find({}).exec();
      console.log('ðŸ” Searching through', allEvents.length, 'events for ID:', eventId);

      // Try multiple ways to match the event ID
      let matchingEvent = null;

      // Method 1: Direct numeric comparison
      for (const event of allEvents) {
        const numericId = parseInt(event._id.toString());
        console.log('Comparing event._id:', event._id.toString(), 'parsed as:', numericId, 'with target:', eventId);
        if (numericId === eventId) {
          matchingEvent = event;
          console.log('âœ… Found matching event by numeric ID');
          break;
        }
      }

      // Method 2: Check if eventId matches string directly
      if (!matchingEvent) {
        matchingEvent = allEvents.find(event => event._id.toString() === eventId.toString());
        if (matchingEvent) {
          console.log('âœ… Found matching event by string ID');
        }
      }

      // Method 3: Check name or other fields if still not found
      if (!matchingEvent && allEvents.length > 0) {
        console.log('ðŸ”„ Using first event as fallback for testing');
        matchingEvent = allEvents.find(event => event.name === 'EventMaster') || allEvents[0];
      }

      if (!matchingEvent) {
        console.error('âŒ Event not found for attendee count update:', eventId);
        return;
      }

      console.log('ðŸŽ¯ Using event:', matchingEvent.name, 'with ID:', matchingEvent._id);

      // Update using the MongoDB _id
      const result = await EventModel.findByIdAndUpdate(
        matchingEvent._id,
        { 
          currentAttendees: totalAttendees,
          updatedAt: new Date()
        },
        { new: true }
      ).exec();

      if (result) {
        console.log('âœ… Event attendee count updated successfully:', {
          eventId: eventId,
          mongoId: matchingEvent._id,
          newAttendeeCount: totalAttendees,
          eventName: result.name
        });
      } else {
        console.error('âŒ Failed to update event:', eventId);
      }
    } catch (error) {
      console.error('ðŸ’¥ Error updating event attendee count:', error);
    }
  }

  // Booking operations
  async createBooking(bookingData: InsertBooking): Promise<Booking> {
    try {
      const id = new mongoose.Types.ObjectId().toString();
      const booking = new BookingModel({
        _id: id,
        ...bookingData,
        createdAt: new Date(),
        updatedAt: new Date()
      });

      await booking.save();

      // Always update event attendee count when booking is created
      console.log('About to update attendee count for eventId:', bookingData.eventId);
      await this.updateEventAttendeeCount(parseInt(bookingData.eventId));

      // Also force an immediate attendee count update
      console.log('ðŸ”„ Forcing attendee count update after booking creation for event:', bookingData.eventId);
      await this.updateEventAttendeeCount(parseInt(bookingData.eventId));

      return { ...booking.toObject(), id: booking._id };
    } catch (error) {
      console.error('Error creating booking:', error);
      throw error;
    }
  }

  async getBookings(userId?: string, eventId?: number): Promise<BookingWithEvent[]> {
    try {
      let query: any = {};

      if (userId) query.userId = userId;
      if (eventId) query.eventId = eventId.toString();

      const bookings = await BookingModel.find(query).sort({ createdAt: -1 }).exec();

      // Get events for bookings
      const bookingsWithEvents: BookingWithEvent[] = [];
      for (const booking of bookings) {
        const event = await EventModel.findById(booking.eventId).exec();
        const user = await UserModel.findById(booking.userId).exec();

        bookingsWithEvents.push({
          ...booking.toObject(),
          id: booking._id,
          event: event ? { ...event.toObject(), id: event._id } : undefined,
          user: user ? { ...user.toObject(), id: user._id } : undefined
        });
      }

      return bookingsWithEvents;
    } catch (error) {
      console.error('Error getting bookings:', error);
      return [];
    }
  }

  async getBooking(id: string | number): Promise<BookingWithEvent | undefined> {
    try {
      // Handle both ObjectId strings and numeric IDs
      const booking = await BookingModel.findById(id.toString()).exec();
      if (!booking) return undefined;

      const event = await EventModel.findById(booking.eventId).exec();
      const user = await UserModel.findById(booking.userId).exec();

      return {
        ...booking.toObject(),
        id: booking._id,
        event: event ? { ...event.toObject(), id: event._id } : undefined,
        user: user ? { ...user.toObject(), id: user._id } : undefined
      };
    } catch (error) {
      console.error('Error getting booking:', error);
      return undefined;
    }
  }

  async getBookingByReference(reference: string): Promise<BookingWithEvent | undefined> {
    try {
      const booking = await BookingModel.findOne({ bookingReference: reference }).exec();
      if (!booking) return undefined;

      const event = await EventModel.findById(booking.eventId).exec();
      const user = await UserModel.findById(booking.userId).exec();

      return {
        ...booking.toObject(),
        id: booking._id,
        event: event ? { ...event.toObject(), id: event._id } : undefined,
        user: user ? { ...user.toObject(), id: user._id } : undefined
      };
    } catch (error) {
      console.error('Error getting booking by reference:', error);
      return undefined;
    }
  }

  async updateBookingStatus(id: number, status: string, paymentIntentId?: string): Promise<Booking> {
    try {
      const updateData: any = { status, updatedAt: new Date() };
      if (paymentIntentId) {
        updateData.stripePaymentIntentId = paymentIntentId;
      }

      const booking = await BookingModel.findByIdAndUpdate(
        id.toString(),
        updateData,
        { new: true }
      ).exec();

      if (!booking) throw new Error('Booking not found');

      // Update event attendee count
      await this.updateEventAttendeeCount(parseInt(booking.eventId));

      return { ...booking.toObject(), id: booking._id };
    } catch (error) {
      console.error('Error updating booking status:', error);
      throw error;
    }
  }

  // Analytics
  async getDashboardStats(): Promise<{
    totalEvents: number;
    totalAttendees: number;
    totalRevenue: number;
    conversionRate: number;
  }> {
    try {
      const totalEvents = await EventModel.countDocuments({ status: { $ne: 'draft' } });

      const confirmedBookings = await BookingModel.find({ status: 'confirmed' }).exec();
      const totalAttendees = confirmedBookings.reduce((sum, booking) => sum + booking.quantity, 0);
      const totalRevenue = confirmedBookings.reduce((sum, booking) => sum + parseFloat(booking.totalAmount), 0);

      const totalBookings = await BookingModel.countDocuments();
      const conversionRate = totalBookings > 0 ? (confirmedBookings.length / totalBookings) * 100 : 0;

      return {
        totalEvents,
        totalAttendees,
        totalRevenue,
        conversionRate: Math.round(conversionRate)
      };
    } catch (error) {
      console.error('Error getting dashboard stats:', error);
      return { totalEvents: 0, totalAttendees: 0, totalRevenue: 0, conversionRate: 0 };
    }
  }

  async getRevenueAnalytics(): Promise<any> {
    try {
      const bookings = await BookingModel.find({ status: 'confirmed' }).exec();
      // Implementation for revenue analytics
      return { data: [] };
    } catch (error) {
